const legalComplianceTopics = [
  {
    type: 'Eviction Process Guide',
    title: 'State-by-State Legal Eviction Process Guide 2024',
    description: 'Step-by-step eviction procedures for all 50 states - avoid costly legal mistakes that could invalidate your case',
    value: '$197 value',
    angle: 'legal_protection',
    pain_point: 'lawsuit_risk'
  },
  {
    type: 'Fair Housing Compliance',
    title: 'Fair Housing Compliance Checklist for Landlords',
    description: 'Protect yourself from discrimination lawsuits with this comprehensive compliance system',
    value: '$147 value',
    angle: 'risk_mitigation',
    pain_point: 'discrimination_claims'
  },
  {
    type: 'Security Deposit Laws',
    title: 'Security Deposit Laws & Return Process by State',
    description: 'Handle security deposits legally and avoid tenant disputes with state-specific procedures',
    value: '$97 value',
    angle: 'dispute_prevention',
    pain_point: 'deposit_disputes'
  },
  {
    type: 'Lease Agreement Templates',
    title: '2024 State-Compliant Lease Agreement Templates',
    description: 'Legally bulletproof lease agreements customized for your state laws and requirements',
    value: '$167 value',
    angle: 'legal_documents',
    pain_point: 'contract_vulnerabilities'
  },
  {
    type: 'Notice Requirements',
    title: 'Legal Notice Requirements for Landlords by State',
    description: 'Proper notice procedures for rent increases, repairs, entry, and termination',
    value: '$77 value',
    angle: 'procedural_compliance',
    pain_point: 'improper_notices'
  },
  {
    type: 'Habitability Standards',
    title: 'Landlord Habitability Requirements & Maintenance Laws',
    description: 'Meet legal habitability standards and avoid tenant withholding rent or breaking leases',
    value: '$87 value',
    angle: 'property_standards',
    pain_point: 'habitability_claims'
  }
];

const selectedMagnet = legalComplianceTopics[Math.floor(Math.random() * legalComplianceTopics.length)];
const quarter = Math.ceil((new Date().getMonth() + 1) / 3);
const year = new Date().getFullYear();

return {
  json: {
    ...selectedMagnet,
    id: 'legal_compliance_' + Date.now(),
    createdAt: new Date().toISOString(),
    targetAudience: 'landlords, property managers, real estate investors',
    category: 'legal_compliance',
    workflow: '12',
    folderPath: `${year}/Q${quarter}/legal-compliance`,
    seoKeywords: 'landlord legal compliance, eviction laws, fair housing, lease agreements'
  }
};

