const scalingTopics = [
  {
    type: 'Portfolio Expansion Strategy',
    title: 'Strategic Property Portfolio Expansion & Growth Planning System',
    description: 'Scale from 1 to 10+ properties with proven expansion strategies and financing techniques',
    value: '$247 value',
    angle: 'growth_acceleration',
    pain_point: 'scaling_challenges'
  },
  {
    type: 'Team Building Framework',
    title: 'Property Management Team Building & Systems Development Guide',
    description: 'Build a management team that runs your portfolio while you focus on growth',
    value: '$197 value',
    angle: 'team_development',
    pain_point: 'management_bottleneck'
  },
  {
    type: 'Systems & Processes',
    title: 'Scalable Property Management Systems & Process Optimization',
    description: 'Create systems that work at scale for effortless portfolio management',
    value: '$177 value',
    angle: 'operational_efficiency',
    pain_point: 'operational_chaos'
  }
];

const selectedMagnet = scalingTopics[Math.floor(Math.random() * scalingTopics.length)];
const quarter = Math.ceil((new Date().getMonth() + 1) / 3);
const year = new Date().getFullYear();

return {
  json: {
    ...selectedMagnet,
    id: 'portfolio_scaling_' + Date.now(),
    createdAt: new Date().toISOString(),
    targetAudience: 'real estate investors, portfolio managers, property entrepreneurs',
    category: 'portfolio_scaling',
    workflow: '13',
    folderPath: `${year}/Q${quarter}/portfolio-scaling`,
    seoKeywords: 'portfolio scaling, property investment growth, real estate expansion, portfolio management'
  }
};

