parameters.jsCode:
const leadMagnetTypes = [
  {
    type: 'Property Management Checklist',
    title: 'Essential Property Management Checklist for New Landlords',
    description: 'Complete 50-point checklist covering tenant screening, maintenance, legal compliance, and rent collection',
    value: '$97 value'
  },
  {
    type: 'Rent Increase Calculator',
    title: 'Legal Rent Increase Calculator & Notice Templates',
    description: 'State-specific rent increase calculators with legal notice templates',
    value: '$47 value'
  },
  {
    type: 'Tenant Screening Kit',
    title: 'Professional Tenant Screening Kit & Background Check Guide',
    description: 'Complete screening process with application forms and evaluation criteria',
    value: '$127 value'
  },
  {
    type: 'Maintenance Tracking System',
    title: 'Property Maintenance Tracking System & Cost Calculator',
    description: 'Spreadsheet system for tracking maintenance costs and scheduling',
    value: '$67 value'
  },
  {
    type: 'Legal Document Templates',
    title: 'Landlord Legal Document Library - 25+ Templates',
    description: 'State-compliant lease agreements, notices, and legal forms',
    value: '$197 value'
  }
];

const selectedMagnet = leadMagnetTypes[Math.floor(Math.random() * leadMagnetTypes.length)];

return {
  json: {
    ...selectedMagnet,
    id: 'magnet_' + Date.now(),
    createdAt: new Date().toISOString(),
    targetAudience: 'new landlords, property managers, real estate investors'
  }
};

