parameters.jsCode:
// Parse Discord interaction data
const body = $input.first().body;

// Handle different Discord interaction types
if (body.type === 3) { // Message component interaction (button/reaction)
  const customId = body.data?.custom_id;
  const messageId = body.message?.id;
  const userId = body.member?.user?.id || body.user?.id;
  const username = body.member?.user?.username || body.user?.username;

  // Extract blog ID from message embed or custom_id
  const embedDescription = body.message?.embeds?.[0]?.description || '';
  const blogIdMatch = embedDescription.match(/\*\*ID:\*\* (blog_\d+)/);
  const blogId = blogIdMatch ? blogIdMatch[1] : null;

  return {
    json: {
      type: 'approval_action',
      action: customId, // 'approve', 'reject', 'request_changes'
      blogId: blogId,
      messageId: messageId,
      userId: userId,
      username: username,
      timestamp: new Date().toISOString()
    }
  };
}

// Handle reaction events (if using reactions instead of buttons)
if (body.t === 'MESSAGE_REACTION_ADD') {
  const emoji = body.d.emoji.name;
  const messageId = body.d.message_id;
  const userId = body.d.user_id;

  let action = 'unknown';
  if (emoji === '‚úÖ') action = 'approve';
  else if (emoji === '‚ùå') action = 'reject';
  else if (emoji === 'üìù') action = 'request_changes';

  return {
    json: {
      type: 'reaction_approval',
      action: action,
      emoji: emoji,
      messageId: messageId,
      userId: userId,
      timestamp: new Date().toISOString()
    }
  };
}

return { json: { type: 'unknown', data: body } };

// Update approval status for the blog post
const approvalData = $('Parse Discord Data').item.json;
const pendingApprovals = JSON.parse($input.first().data || '[]');

// Find and update the specific blog post
let updated = false;
const updatedApprovals = pendingApprovals.map(approval => {
  if (approval.blogId === approvalData.blogId) {
    updated = true;
    return {
      ...approval,
      status: 'approved',
      approvedBy: approvalData.username,
      approvedAt: new Date().toISOString(),
      discordUserId: approvalData.userId
    };
  }
  return approval;
});

// If not found in existing approvals, create new entry
if (!updated && approvalData.blogId) {
  updatedApprovals.push({
    blogId: approvalData.blogId,
    status: 'approved',
    approvedBy: approvalData.username,
    approvedAt: new Date().toISOString(),
    discordUserId: approvalData.userId,
    messageId: approvalData.messageId
  });
}

return {
  json: {
    approvals: updatedApprovals,
    blogId: approvalData.blogId,
    approvedBy: approvalData.username
  }
};

// Handle rejection - mark as rejected and log reason
const approvalData = $('Parse Discord Data').item.json;
const pendingApprovals = JSON.parse($('Read Pending Approvals').first().data || '[]');

// Update or add rejection status
let updated = false;
const updatedApprovals = pendingApprovals.map(approval => {
  if (approval.blogId === approvalData.blogId) {
    updated = true;
    return {
      ...approval,
      status: 'rejected',
      rejectedBy: approvalData.username,
      rejectedAt: new Date().toISOString(),
      discordUserId: approvalData.userId
    };
  }
  return approval;
});

if (!updated && approvalData.blogId) {
  updatedApprovals.push({
    blogId: approvalData.blogId,
    status: 'rejected',
    rejectedBy: approvalData.username,
    rejectedAt: new Date().toISOString(),
    discordUserId: approvalData.userId,
    messageId: approvalData.messageId
  });
}

return {
  json: {
    approvals: updatedApprovals,
    blogId: approvalData.blogId,
    rejectedBy: approvalData.username
  }
};

