// Parse CSV data and identify leads for nurturing
const csvData = $input.first().data;
const lines = csvData.split('\n').filter(line => line.trim());
const headers = lines[0].split(',');

const leads = [];
for (let i = 1; i < lines.length; i++) {
  const values = lines[i].split(',');
  if (values.length === headers.length) {
    const lead = {};
    headers.forEach((header, index) => {
      lead[header.trim()] = values[index]?.trim() || '';
    });

    // Only process leads from the last 7 days
    const signupDate = new Date(lead.signup_date);
    const daysSinceSignup = Math.floor((Date.now() - signupDate.getTime()) / (1000 * 60 * 60 * 24));

    if (daysSinceSignup >= 0 && daysSinceSignup <= 7) {
      lead.daysSinceSignup = daysSinceSignup;
      lead.engagementStage = daysSinceSignup <= 1 ? 'new' : 
                           daysSinceSignup <= 3 ? 'early' :
                           daysSinceSignup <= 7 ? 'nurture' : 'late';
      leads.push(lead);
    }
  }
}

return leads.map(lead => ({ json: lead }));

// Parse email content and prepare for sending
const response = $input.first().json;
const emailContent = response.response;

const subjectMatch = emailContent.match(/SUBJECT:\s*(.+)/i);
const bodyMatch = emailContent.match(/BODY:\s*([\s\S]+)/i);

const subject = subjectMatch ? subjectMatch[1].trim() : 'Follow-up from TenantFlow';
const body = bodyMatch ? bodyMatch[1].trim() : emailContent;

// Get lead data from previous node
const leadData = $('Parse Lead Data').item.json;

return {
  json: {
    to: leadData.email,
    subject: subject,
    body: body,
    leadName: leadData.name,
    engagementStage: leadData.engagementStage,
    daysSinceSignup: leadData.daysSinceSignup
  }
};

