// Calculate lead scores based on activity
const activityData = JSON.parse($input.first().data || '[]');

const scoredLeads = activityData.map(user => {
  let score = 0;

  // Scoring factors
  if (user.login_count > 5) score += 20;
  else if (user.login_count > 2) score += 10;
  else if (user.login_count > 0) score += 5;

  if (user.properties_added > 0) score += 30;
  if (user.tenants_invited > 0) score += 25;
  if (user.payments_processed > 0) score += 35;
  if (user.maintenance_requests > 0) score += 15;

  // Time-based scoring
  const daysSinceLastLogin = Math.floor((Date.now() - new Date(user.last_login).getTime()) / (1000 * 60 * 60 * 24));
  if (daysSinceLastLogin <= 1) score += 15;
  else if (daysSinceLastLogin <= 3) score += 10;
  else if (daysSinceLastLogin <= 7) score += 5;
  else if (daysSinceLastLogin > 14) score -= 10;

  // Engagement quality
  if (user.session_duration_avg > 300) score += 10; // 5+ minutes
  if (user.page_views > 20) score += 10;

  // Categorize leads
  let category = 'cold';
  if (score >= 70) category = 'hot';
  else if (score >= 40) category = 'warm';
  else if (score >= 20) category = 'lukewarm';

  return {
    ...user,
    leadScore: score,
    category: category,
    daysSinceLastLogin: daysSinceLastLogin,
    scoringDate: new Date().toISOString()
  };
});

// Sort by score descending
scoredLeads.sort((a, b) => b.leadScore - a.leadScore);

return scoredLeads.map(lead => ({ json: lead }));

